import unittest

from solution import is_valid


# Tests adapted from `problem-specifications//canonical-data.json` @ v1.1.0

class SherlockTest(unittest.TestCase):
    def test_basic(self):
        self.assertEqual(is_valid('aabbcc'), True)

    def test_invalid_count(self):
        self.assertEqual(is_valid('aabbcccc'), False)

    def test_invalid_lets(self):
        self.assertEqual(is_valid('aabbccde'), False)

    def test_invalid_multiple(self):
        self.assertEqual(is_valid('aabbccdeee'), False)

    def test_invalid_long(self):
        self.assertEqual(is_valid('hfchdkkbfifgbgebfaahijchgeeeiagkadjfcbekbdaifchkjfejckbiiihegacfbchdihkgbkbddgaefhkdgccjejjaajgijdkd'),
                True)

    def test_invalid_longer(self):
        self.assertEqual(is_valid(''),
                True)

if __name__ == '__main__':
    unittest.main()
